import React, { useEffect } from 'react'
import TrackVisibility from 'react-on-screen';
import 'animate.css';
import menu1 from '../img/menu1.jpeg'
import menu2 from '../img/menu2.jpeg'

import { useState } from 'react';

export default function Menu() {
    const [ dispState, setDispState ] = useState('none')
    // const [ background, setBackground ] = useState('none')
    // const [ wid, setWidth ] = useState(0)

    const showImgPreview = () => {
        setDispState('block');
        // setBackground('rgba(0, 0, 0, 0.5)');
        // setWidth(window.innerWidth);
    }

    const closeImgPreview = () => {
        setDispState('none');
        // setBackground('none');
        // setWidth(0);
    }

    useEffect(() => {
        document.documentElement.style.setProperty("--menu1-dis", dispState);
    }, [ dispState ])

    return (
        <div className='menu' id='menu'>
            <TrackVisibility>
                {({ isVisible }) =>
                    <div className={isVisible ? 'animate__animated animate__fadeIn' : ''}> 
                        <h1>Menu</h1> 
                    </div>
                }       
            </TrackVisibility>
            <div className='menu-container'>
                <div className='menu-box'>
                    <img src={menu1} onClick={showImgPreview}></img>
                </div>
                <div className='menu-box'>
                    <img src={menu2}></img>
                </div>
            </div>
            <div className='menu1-preview'>
            {/* style={{display: dispState, width : wid , background : background}} */}
                <img src={menu1}  onClick={closeImgPreview}></img>
            </div>
        </div>
    )
}
